// Génération du rapport quotidien
const kpis = $input.all()[0].json;
const missed = $input.all()[1].json;
const marques = $input.all()[2].json;

// Formatage KPIs
const opportunites_24h = kpis.opportunites_24h || 0;
const a_acheter = kpis.a_acheter || 0;
const en_vente = kpis.en_vente || 0;
const vendus = kpis.vendus_total || 0;
const score_moyen = (kpis.score_moyen * 100).toFixed(1) || 0;
const capital = kpis.capital_engage || 0;
const ca = kpis.ca_total || 0;
const profit = kpis.profit_total || 0;
const roi_global = capital > 0 ? ((profit / capital) * 100).toFixed(1) : 0;

// Top 3 marques
const top_marques = marques.slice(0, 3).map((m, i) => 
  `${i + 1}. ${m.marque?.toUpperCase() || 'N/A'} - ${m.nombre_vendus || 0} vendus - ${(m.roi_moyen * 100).toFixed(0)}% ROI`
).join('\n');

// Opportunités manquées
const missed_list = missed.slice(0, 5).map(m => 
  `• ${m.titre?.substring(0, 40) || 'N/A'} - ${m.prix_achat}€ (score: ${(m.score * 100).toFixed(0)}%)`
).join('\n');

const rapport = `📊 *Rapport Quotidien FlipFinder*
━━━━━━━━━━━━━━━━━━━━━━━━━━

📈 *KPIs Globaux*
• Opportunités détectées (24h) : ${opportunites_24h}
• À acheter : ${a_acheter}
• En vente : ${en_vente}
• Vendus (total) : ${vendus}
• Score moyen : ${score_moyen}%

💰 *Financier*
• Capital engagé : ${capital}€
• CA total : ${ca}€
• Profit total : ${profit}€
• ROI global : ${roi_global}%

🏆 *Top 3 Marques*
${top_marques || 'Aucune donnée'}

⚠️ *Top 5 Opportunités Manquées*
${missed_list || 'Aucune'}

━━━━━━━━━━━━━━━━━━━━━━━━━━
🤖 FlipFinder - ${new Date().toLocaleDateString('fr-FR')}`;

return [{
  json: {
    rapport,
    kpis: {
      opportunites_24h,
      a_acheter,
      en_vente,
      vendus,
      capital,
      ca,
      profit,
      roi_global
    }
  }
}];
