// Extraction et scoring des r√©sultats
const data = $input.first().json;
const html = data.html || data.body || '';
const keyword = $input.first().json.keyword;

// V√©rifier si Cloudflare est pr√©sent
if (html.includes('challenge-platform') || html.includes('cf-browser-verification')) {
  return [{ json: { error: 'Cloudflare d√©tect√©', keyword, html_preview: html.substring(0, 500) } }];
}

// Patterns de d√©tection
const MARQUES_PREMIUM = {
  'hilti': 2.5,
  'festool': 2.3,
  'milwaukee': 2.1,
  'makita': 2.0,
  'dewalt': 1.9,
  'bosch': 1.7,
  'metabo': 1.8
};

const SIGNAUX_LIQUIDATION = [
  { pattern: /liquidation\s+judiciaire/i, score: 0.3 },
  { pattern: /cessation\s+activit[√©e]/i, score: 0.3 },
  { pattern: /fermeture\s+entreprise/i, score: 0.25 },
  { pattern: /d[√©e]part\s+retraite/i, score: 0.2 },
  { pattern: /urgent/i, score: 0.15 }
];

// Parse HTML - Adaptation pour Interencheres
const results = [];

// Patterns Interencheres (√† adapter selon structure r√©elle)
const patterns = [
  // Structure de base Interencheres
  /<article[^>]*class="[^"]*lot[^"]*"[^>]*>([\s\S]*?)<\/article>/gi,
  // Fallback si structure diff√©rente
  /<div[^>]*class="[^"]*result[^"]*"[^>]*>([\s\S]*?)<\/div>/gi
];

for (const regex of patterns) {
  let match;
  while ((match = regex.exec(html)) !== null) {
    const itemHtml = match[1];

    // Extraction titre (plusieurs patterns possibles)
    let titre = '';
    const titlePatterns = [
      /<h[2-4][^>]*>([^<]+)<\/h[2-4]>/i,
      /<[^>]*class="[^"]*title[^"]*"[^>]*>([^<]+)</i,
      /<a[^>]*title="([^"]+)"/i
    ];

    for (const tp of titlePatterns) {
      const tm = tp.exec(itemHtml);
      if (tm) {
        titre = tm[1].trim();
        break;
      }
    }

    if (!titre) continue;

    // Extraction prix
    const priceMatch = /(\d+(?:[\s,]\d{3})*)\s*‚Ç¨/i.exec(itemHtml);
    const prix = priceMatch ? parseInt(priceMatch[1].replace(/[\s,]/g, '')) : 0;

    // Extraction lien
    const linkMatch = /href="([^"]+)"/i.exec(itemHtml);
    let url = linkMatch ? linkMatch[1] : '';
    if (url && !url.startsWith('http')) {
      url = 'https://www.interencheres.com' + url;
    }

    // Calcul du score
    let score = 0;
    let marque_detectee = null;
    let multiplicateur = 1;

    // D√©tection marque
    for (const [marque, mult] of Object.entries(MARQUES_PREMIUM)) {
      if (titre.toLowerCase().includes(marque)) {
        marque_detectee = marque;
        multiplicateur = mult;
        score += 0.4;
        break;
      }
    }

    // D√©tection signaux liquidation
    for (const signal of SIGNAUX_LIQUIDATION) {
      if (signal.pattern.test(titre)) {
        score += signal.score;
      }
    }

    // Score prix (plus c'est bas, mieux c'est)
    if (prix > 0 && prix < 100) score += 0.3;
    else if (prix < 200) score += 0.2;
    else if (prix < 500) score += 0.1;

    // Estimation ROI
    const valeur_estimee = prix * multiplicateur;
    const roi_estime = multiplicateur - 1;

    if (score > 0.5) {
      results.push({
        json: {
          titre,
          prix,
          url,
          keyword,
          score: Math.min(score, 1),
          marque_detectee,
          multiplicateur,
          valeur_estimee,
          roi_estime,
          statut: 'DETECTE',
          date_detection: new Date().toISOString()
        }
      });
    }
  }

  if (results.length > 0) break;
}

return results.length > 0 ? results : [{ json: { message: 'Aucun r√©sultat pertinent', keyword, html_length: html.length } }];

