return keywords.map(keyword => ({ json: { keyword } }));

// N≈ìud de synth√®se et validation des donn√©es
const items = $input.all();
const keywords = $('Mots-cl√©s Premium').all();
const synthese = [];

for (let i = 0; i < items.length; i++) {
  const data = items[i].json;

  // R√©cup√©rer le keyword correspondant depuis le n≈ìud "Mots-cl√©s Premium"
  const keyword = keywords[i] ? keywords[i].json.keyword : 'unknown';

  const html = data.html || data.body || '';
  const success = data.success !== undefined ? data.success : false;
  const cloudflareDetected = data.cloudflareDetected || false;

  // Statistiques HTML
  const htmlLength = html.length;
  const hasContent = htmlLength > 10000;

  // D√©tection de contenu r√©el
  const hasLots = html.includes('class="lot') || html.includes('result') || html.includes('<article');
  const hasKeyword = keyword ? html.toLowerCase().includes(keyword.toLowerCase()) : false;

  synthese.push({
    json: {
      keyword: keyword,
      success,
      cloudflareDetected,
      htmlLength,
      hasContent,
      hasLots,
      hasKeyword,
      status: success && !cloudflareDetected && hasContent ? '‚úÖ OK' : '‚ö†Ô∏è Issue',
      html: html,  // Conserver le HTML pour le n≈ìud suivant
      timestamp: new Date().toISOString()
    }
  });
}

return synthese;

// Extraction bas√©e sur la structure HTML Interencheres
const items = $input.all();
const allResults = [];

// Liste des marques premium pour d√©tection
const MARQUES_PREMIUM = ['hilti', 'festool', 'milwaukee', 'makita', 'dewalt', 'bosch', 'metabo'];

// Traiter chaque item (chaque keyword)
for (let i = 0; i < items.length; i++) {
  const data = items[i].json;
  const html = data.html || '';
  const keyword = data.keyword || 'unknown';

  // V√©rifier Cloudflare CHALLENGE
  const isCloudflareChallenge = (
    (html.includes('cf-browser-verification') && html.includes('Checking your browser')) ||
    (html.includes('challenge-platform') && html.includes('Just a moment')) ||
    (html.includes('Cloudflare') && html.length < 5000)
  );

  if (isCloudflareChallenge) {
    allResults.push({ 
      json: { 
        error: 'Cloudflare d√©tect√©', 
        keyword, 
        html_preview: html.substring(0, 500) 
      } 
    });
    continue;
  }

  // EXTRACTION DES LOTS - Structure Interencheres r√©elle
  const lotPattern = /<a[^>]*href="([^"]*\/lot-\d+\.html)"[^>]*id="(\d+)"[^>]*>([\s\S]*?)<\/a>/gi;

  let match;
  let lotsFound = 0;

  while ((match = lotPattern.exec(html)) !== null) {
    const url = 'https://www.interencheres.com' + match[1];
    const lotId = match[2];
    const lotContent = match[3];

    // Extraction titre
    const titreMatch = /<div[^>]*class="[^"]*title[^"]*text-subtitle-2[^"]*"[^>]*>\s*<div[^>]*>([^<]+)<\/div>/i.exec(lotContent);
    if (!titreMatch) continue;

    const titre = titreMatch[1].trim();

    // Extraction estimation du vendeur (optionnelle)
    let estimation_vendeur_min = null;
    let estimation_vendeur_max = null;
    const estimationVendeurMatch = /Estimation\s*:\s*<\/span>\s*<span[^>]*>(\d+)(?:&nbsp;)?\s*‚Ç¨\s*-\s*(\d+)(?:&nbsp;)?\s*‚Ç¨/i.exec(lotContent);
    if (estimationVendeurMatch) {
      estimation_vendeur_min = parseInt(estimationVendeurMatch[1]);
      estimation_vendeur_max = parseInt(estimationVendeurMatch[2]);
    }

    // Extraction prix - Mise √† prix
    let prix = 0;
    const miseAPrixMatch = /Mise\s+√†\s+prix\s*:\s*<\/span>\s*<span[^>]*>(\d+)(?:&nbsp;)?\s*‚Ç¨/i.exec(lotContent);
    if (miseAPrixMatch) {
      prix = parseInt(miseAPrixMatch[1]);
    } else if (estimation_vendeur_min) {
      prix = estimation_vendeur_min;
    }

    // D√©tection marque uniquement
    let marque_detectee = null;
    for (const marque of MARQUES_PREMIUM) {
      if (titre.toLowerCase().includes(marque)) {
        marque_detectee = marque;
        break;
      }
    }

    // Ajouter le lot (pas de filtrage)
    allResults.push({
      json: {
        titre,
        prix,
        estimation_vendeur_min,
        estimation_vendeur_max,
        url,
        lot_id: lotId,
        keyword,
        marque_detectee,
        date_detection: new Date().toISOString()
      }
    });
    lotsFound++;
  }

  // Si aucun r√©sultat trouv√© pour ce keyword
  if (lotsFound === 0) {
    allResults.push({ 
      json: { 
        message: 'Aucun lot trouv√©', 
        keyword, 
        html_length: html.length,
        has_autoqa_class: html.includes('autoqa-sale-details-item'),
        has_lot_links: html.includes('/lot-')
      } 
    });
  }
}

return allResults.length > 0 ? allResults : [{ json: { message: 'Aucun item trait√©' } }];
